import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  TextInput,
  Button,
  SafeAreaView,
} from "react-native";
import { Formik, Form, Field } from "formik";

import * as Yup from "yup";

import { useDispatch } from "react-redux";

import { createUser } from "../store/actions/userActions";

const Register = ({ id, name, email, password,passwordConfirmation,isValid }) => {
  const dispatch = useDispatch();

  return (
    <View style={styles.loginContainer}>
      <Formik
        initialValues={{
          name,
          email,
          password,
          passwordConfirmation
        }}
        validationSchema={Yup.object({
          name: Yup.string()
            .min(4, "Debe tener al menos 4 caracteres")
            .max(50, "Debe tener 50 caracteres o menos")
            .required("Debes completar este campo"),
          email: Yup.string()
            .email("Introduzca un email valido por favor")
            .required("Debes completar este campo"),
          address: Yup.string()
            .min(6, "Debe tener al menos 4 caracteres")
            .max(50, "Debe tener 50 caracteres o menos")
            .required("Debes completar este campo"),
          phoneNumber: Yup.string()
            .required("Please Enter your Phone Number")
            .matches(
              /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/,
              "Phone number is not valid"
            ),
          password: Yup.string()
            .required("Please Enter your password")
            .matches(
              /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/,
              "Must Contain 8 Characters, One Uppercase, One Lowercase, One Number and one special case Character"
            ),
          passwordConfirmation: Yup.string()
            .oneOf([Yup.ref("password"), null], "La contraseña no coincide")
            .required("Password confirm is required"),
        })}
        onSubmit={async (values, { setSubmitting, resetForm }) => {
          dispatch(createUser(values)).then((response) => {
            resetForm();
            setSubmitting(false);
          });
        }}
      >
        {({ handleChange, handleSubmit, values, handleBlur, errors }) => (
          <View>
            <TextInput
              name="name"
              placeholder="name"
              onChangeText={handleChange("name")}
              onBlur={handleBlur("name")}
              style={styles.textInput}
              value={values.name}
              keyboardType="text"
            />
            {errors.name && (
              <Text style={{ fontSize: 10, color: "red" }}>{errors.name}</Text>
            )}
            <TextInput
              name="email"
              placeholder="email"
              onChangeText={handleChange("email")}
              onBlur={handleBlur("email")}
              style={styles.textInput}
              value={values.email}
              keyboardType="email-address"
            />
            {errors.email && (
              <Text style={{ fontSize: 10, color: "red" }}>{errors.email}</Text>
            )}
            <TextInput
              name="password"
              placeholder="Password"
              onChangeText={handleChange("password")}
              onBlur={handleBlur("password")}
              style={styles.textInput}
              value={values.password}
              secureTextEntry={true}
            />
            {errors.password && (
              <Text style={{ fontSize: 10, color: "red" }}>
                {errors.password}
              </Text>
            )}
            <TextInput
              name="passwordConfirmation"
              placeholder="Confirm password"
              onChangeText={handleChange("passwordConfirmation")}
              onBlur={handleBlur("passwordConfirmation")}
              style={styles.textInput}
              value={values.passwordConfirmation}
              secureTextEntry={true}
            />
            {errors.passwordConfirmation && (
              <Text style={{ fontSize: 10, color: "red" }}>
                {errors.passwordConfirmation}
              </Text>
            )}
            <Button
              secureTextEntry={true}
              title="Register"
              onPress={handleSubmit}
              disabled={!isValid}
            />
          </View>
        )}
      </Formik>
    </View>
  );
};

const styles = StyleSheet.create({
  loginContainer: {
    width: "100%",
    alignItems: "center",
    backgroundColor: "white",
    padding: 10,
    elevation: 10,
    backgroundColor: "#e6e6e6",
  },
  textInput: {
    height: 40,
    width: "100%",
    margin: 10,
    backgroundColor: "white",
    borderColor: "gray",
    borderWidth: StyleSheet.hairlineWidth,
    borderRadius: 10,
  },
});

export default Register;
